class Student {
    id;
    fullname;
    gender;
    date;
    birthplace;
    email;
    contact;
    address;
    country;
    img;

    constructor(id, fullname, gender, date, birthplace, email, contact, adress, country, img) {
        this.id = id;
        this.fullname = fullname;
        this.gender = gender;
        this.date = date;
        this.birthplace = birthplace;
        this.email = email;
        this.contact = contact;
        this.address = adress;
        this.country = country;
        this.img = img;
    }

    getID() {
        return this.id;
    }

    getFullname() {
        return this.fullname
    }

    getGender() {
        return this.gender;
    }

    getDate() {
        return this.date;
    }

    getBirdPlace() {
        return this.birthplace;
    }

    getEmail() {
        return this.email;
    }

    getContact() {
        return this.contact;
    }

    getAdress() {
        return this.address;
    }

    getCountry() {
        return this.country;
    }

    getImg() {
        return this.img;
    }

    setID(id) {
        this.id = id;
    }

    setFullname(fullname) {
        this.fullname = fullname;
    }

    setGender(gender) {
        this.gender = gender;
    }

    setDate(date) {
        this.date = date;
    }

    setBirdPlace(birdplace) {
        this.birthplace = birdplace;
    }

    setEmail(email) {
        this.email = email;
    }

    setContact(contact) {
        this.contact = contact;
    }

    setAdress(adress) {
        this.address = adress;
    }

    setCountry(country) {
        this.country = country;
    }

    setImg(img) {
        this.img = img;
    }
}

