//Function add student
function addStudent() {
    let id = document.getElementById('identify').value;
    let fullname = document.getElementById("fullname").value;
    let gender = document.getElementById('gender').value;
    let date = document.getElementById('dateofbirth').value;
    let birthplace = document.getElementById('birthplace').value
    let email = document.getElementById('email').value;
    let contact = document.getElementById('phone').value;
    let adress = document.getElementById('adress').value;
    let country = document.getElementById('country').value;
    let img = document.getElementById('img').value;
    if (id===''||fullname===''||gender===''||date===''||birthplace===''||email===''||contact===''||adress===''||country===''){
        alert('Có thông tin bạn chưa nhập, hãy kiểm tra và nhập lại')
        return;
    }
    if (checkStudentID(id) === true) {
        let student = new Student(id, fullname, gender, date, birthplace, email, contact, adress, country, img);
        studentArr.push(student);
        localStorage.setItem("studentArray", JSON.stringify(studentArr));
        display(studentArr);
        resetInput();
        displayOldest(studentArr);
    }
}

//Kiểm tra ID có trùng nhau
function checkStudentID(studentid) {
    for (let i = 0; i < studentArr.length; i++) {
        if (studentArr[i].id === studentid) {
            alert('Bạn nhập bị trùng , mời bạn nhập lại');
            return false;
        }
    }
    return true;
}

//Hiện ra thông tin trong mảng chứa các đối tượng của class Student
function display(arr) {
    let html = '';
    for (let i = 0; i < arr.length; i++) {
        html += `<tr class="border">
              
                    <td>${arr[i].id}</td>
                    <td>${arr[i].fullname}</td>
                    <td>${arr[i].gender}</td>
                    <td>${convertDateFormat(arr[i].date)}</td>
                    <td>${arr[i].birthplace}</td>
                    <td>${arr[i].email}</td>
                    <td>${arr[i].contact}</td>
                    <td>${arr[i].address}</td>
                    <td>${arr[i].country}</td>
                    <td><img width="120px" height="100px" src="${arr[i].img}" alt="emptyAvatar"></td>
                    <td>
                    <button class="edit-del-button" onclick="editStudent(${i})">Edit</button>
                    <button class="edit-del-button" onclick="deleteStudent(${i})">Delete</button>
                    </td>
                    </tr>       
                    `
    }
    document.getElementById("list").innerHTML = html;
}

// Reset ô input
function resetInput() {
    document.getElementById('identify').value = '';
    document.getElementById("fullname").value = '';
    document.getElementById('gender').value = '';
    document.getElementById('dateofbirth').value = '';
    document.getElementById('birthplace').value = '';
    document.getElementById('email').value = '';
    document.getElementById('phone').value = '';
    document.getElementById('adress').value = '';
    document.getElementById('country').value = '';
    document.getElementById('img').value = '';
}

let studentID;

//Sửa lại thông tin student
function editStudent(index) {
    document.getElementById('identify').value = studentArr[index].getID();
    document.getElementById("fullname").value = studentArr[index].getFullname();
    document.getElementById('gender').value = studentArr[index].getGender();
    document.getElementById('dateofbirth').value = studentArr[index].getDate();
    document.getElementById('birthplace').value = studentArr[index].getBirdPlace();
    document.getElementById('email').value = studentArr[index].getEmail();
    document.getElementById('phone').value = studentArr[index].getContact();
    document.getElementById('adress').value = studentArr[index].getAdress();
    document.getElementById('country').value = studentArr[index].getCountry();
    document.getElementById('img').value = studentArr[index].getImg();
    studentID = index;
    console.log(`Student ID sau là ${studentID}`)
}

// Save thông tin student vừa sửa
function saveInfo() {
    studentArr[studentID].setID(document.getElementById('identify').value);
    studentArr[studentID].setFullname(document.getElementById("fullname").value);
    studentArr[studentID].setGender(document.getElementById('gender').value);
    studentArr[studentID].setDate(document.getElementById('dateofbirth').value);
    studentArr[studentID].setBirdPlace(document.getElementById('birthplace').value);
    studentArr[studentID].setEmail(document.getElementById('email').value);
    studentArr[studentID].setContact(document.getElementById('phone').value);
    studentArr[studentID].setAdress(document.getElementById('adress').value);
    studentArr[studentID].setCountry(document.getElementById('country').value);
    studentArr[studentID].setImg(document.getElementById('img').value);
    display(studentArr);
    resetInput();
    localStorage.setItem("studentArray", JSON.stringify(studentArr));
}

// Hàm thực hiện xoá sinh viên;
function deleteStudent(index) {
    studentArr.splice(index, 1);
    display(studentArr);
    localStorage.setItem("studentArray", JSON.stringify(studentArr));
}

//Chức năng tìm kiếm tên ( nhập tên để tìm kiếm hoặc nhập menu để trả về toàn bộ danh sách)
function findStudent() {
    let nameFinder = prompt('Nhập tên trên danh sách bạn muốn tìm');
    if (nameFinder !== 'menu') {
        display(findingArr(studentArr, nameFinder));
    } else {
        display(studentArr);
    }
}

function findingArr(studentArr, Finder) {
    let findStudentResult = [];
    for (let i = 0; i < studentArr.length; i++) {
        for (let j = 0; j < studentArr[i].fullname.length; j++) {
            if (studentArr[i].fullname.indexOf(Finder) >= 0) {
                findStudentResult.push((studentArr[i]));
                break;
            }
        }
    }
    return findStudentResult;
}

// Thêm input quê quán "nơi khác"
function inputLocation() {
    let temp;
    let selectBox = document.getElementById('country');
    if (selectBox.value === 'Nơi khác') {
        let inputAnotherLocation = prompt('Nhập quê quán của bạn');
        let addNewOption = document.createElement('option');
        //Chuyển vị trí với Nơi khác
        addNewOption.text = inputAnotherLocation;
        addNewOption.value = inputAnotherLocation;
        selectBox.add(addNewOption);
        selectBox.value = inputAnotherLocation;
        temp = selectBox[selectBox.length - 1];
        selectBox[selectBox.length - 1] = selectBox[selectBox.length - 2];
        selectBox[selectBox.length - 2] = temp;
    }
}

function displayOldest(arr) {
    let displayStudentArr = [];
    for (let i = 0; i < arr.length; i++) {
        displayStudentArr[i] = arr[i];
    }
    displayStudentArr.sort(function (a, b) {
        return new Date(a.date) - new Date(b.date)
    })
    let oldestStudent = '';
    for (let i = 0; i < 3; i++) {
        oldestStudent += `<tr>
                    <td>${displayStudentArr[i].id}</td>
                     <td>${displayStudentArr[i].fullname}</td>
                     <td>${convertDateFormat(displayStudentArr[i].date)}</td>
                 </tr>`
    }
    document.getElementById('oldestStudent').innerHTML = oldestStudent;
    let youngestStudent = ``
    for (let i = displayStudentArr.length - 3; i <= displayStudentArr.length - 1; i++) {
        youngestStudent +=
            `<tr>
                     <td>${displayStudentArr[i].id}</td>
                     <td>${displayStudentArr[i].fullname}</td>
                     <td>${convertDateFormat(displayStudentArr[i].date)}</td>
                  </tr>
                `
    }
    document.getElementById('youngestStudent').innerHTML = youngestStudent;
}

function convertDateFormat(dateString) {
    // Tách các thành phần ngày, tháng, năm
    let parts = dateString.split('/');

    // Lấy các thành phần ngày, tháng, năm
    let year = parts[0];
    let month = parts[1];
    let day = parts[2];

    // Tạo chuỗi mới với định dạng dd/mm/yyyy
    let newDateString = `${day}/${month}/${year}`;

    // Trả về chuỗi ngày tháng mới
    return newDateString;
}

